function.arguments:
{"node_name": "add_pscale", "run_over": "Points", "vex_code": "// 给散点添加随机大小
@pscale = fit01(rand(@ptnum), 0.5, 1.2);
// 随机旋转角度
@orient = quaternion(radians(rand(@ptnum * 13) * 360), {0, 1, 0});"}

{"node_name": "wind_animation", "run_over": "Points", "vex_code": "// 风吹草动画效果
float time = @Time;

// 获取点在草叶上的相对高度 (底部固定，顶部摆动最大)
float height_factor = @P.y;

// 风的主方向和强度
vector wind_dir = normalize(set(1, 0, 0.3));
float wind_strength = 0.3;

// 多层噪波叠加，模拟自然风
float noise1 = noise(@P * 0.5 + time * 0.8);  // 大范围慢速波动
float noise2 = noise(@P * 2.0 + time * 2.0) * 0.3;  // 中等细节
float noise3 = noise(@P * 5.0 + time * 4.0) * 0.1;  // 高频抖动

float wind_noise = (noise1 + noise2 + noise3 - 0.5) * 2;

// 应用位移 - 高度越高摆动越大
vector displacement = wind_dir * wind_noise * wind_strength * height_factor * height_factor;

@P += displacement;

// 添加轻微的颜色变化
@Cd = set(0.2, 0.5 + noise1 * 0.3, 0.1);"}
